define ('x_ite/Base/X3DCast',[
   "x_ite/Fields",
],
function (Fields)
{
"use strict";

   return function (type, node, innerNode = true)
   {
      try
      {
         if (node)
         {
            if (node instanceof Fields .SFNode)
               node = node .getValue ();

            if (node)
            {
               if (innerNode)
                  node = node .getInnerNode ();

               if (node .getType () .indexOf (type) !== -1)
                  return node;
            }
         }
      }
      catch (error)
      { }

      return null;
   };
});

