                  stream .string += " ";
                  stream .string += "type='";
                  stream .string += field .getTypeName ();
                  stream .string += "'";
                  stream .string += " ";
                  stream .string += "name='";
                  stream .string += generator .XMLEncode (field .getName ());
                  stream .string += "'";

