            0x8B50: 'vec2',
            0x8B51: 'vec3',
            0x8B52: 'vec4',
            0x8B53: 'ivec2',
            0x8B54: 'ivec3',
            0x8B55: 'ivec4',
            0x8B56: 'bool',
            0x8B57: 'bvec2',
            0x8B58: 'bvec3',
            0x8B59: 'bvec4',
            0x8B5A: 'mat2',
            0x8B5B: 'mat3',
            0x8B5C: 'mat4',
            0x8B5E: 'sampler2D',
            0x8B60: 'samplerCube',
            0x1400: 'byte',
            0x1401: 'ubyte',
            0x1402: 'short',
            0x1403: 'ushort',
            0x1404: 'int',
            0x1405: 'uint',
            0x1406: 'float',
