            proto .setup ();

            try
            {
               const existingProto = this .getExecutionContext () .getProtoDeclaration (name);

               this .getExecutionContext () .updateProtoDeclaration (this .getExecutionContext () .getUniqueProtoName (name), existingProto);
            }
