      this .min                      = new Vector3 (0, 0, 0);
      this .max                      = new Vector3 (0, 0, 0);
      this .bbox                     = new Box3 (this .min, this .max, true);
      this .solid                    = true;
      this .primitiveMode            = browser .getContext () .TRIANGLES;
      this .geometryType             = 3;
      this .flatShading              = undefined;
      this .colorMaterial            = false;
      this .attribNodes              = [ ];
      this .attribArrays             = [ ];
