               p2 = line .point,
               d1 = this .direction,
               d2 = line .direction;

            const theta = d1 .dot (d2); // angle between both lines

            if (Math .abs (theta) >= 1)
               return false; // lines are parallel

            u .assign (p2) .subtract (p1);

            const t = (u .dot (d1) - theta * u .dot (d2)) / (1 - theta * theta);

