                  // gl .bindBuffer (gl .ARRAY_BUFFER, this .trianglesBuffer);
                  // gl .getBufferSubData (gl .ARRAY_BUFFER, 0, data);
                  // console .log (data);

                  // Render triangles.

                  if (blendModeNode)
                     blendModeNode .enable (gl);

