                        fogCoordOffset = 0,
                        colorOffset    = 1 * Float32Array .BYTES_PER_ELEMENT,
                        texCoordOffset = 5 * Float32Array .BYTES_PER_ELEMENT,
                        vertexOffset   = 9 * Float32Array .BYTES_PER_ELEMENT;

