define ('standard/Time/MicroTime',[],function ()
{
"use strict";

   // Return a pseudo accurate timestamp.

   performance .now = (function ()
   {
      const now = performance .now;

      let
         offset = 0,
         last   = 0;

      return function ()
      {
         const current = now .call (this);

         if (current > last)
         {
            offset = 0;
            last   = current;

            return current;
         }
         else
         {
            return last = current + (++ offset / 1000);
         }
      };
   })();
});

