define ('x_ite/Bits/X3DCast',[
	"x_ite/Fields",
],
function (Fields)
{
"use strict";

	return function (type, node)
	{
		try
		{
			if (node)
			{
				if (node instanceof Fields .SFNode)
					node = node .getValue ();

				if (node)
				{
					node = node .getInnerNode ();
				
					if (node .getType () .indexOf (type) !== -1)
						return node;
				}
			}
		}
		catch (error)
		{ }

		return null;
	};
});

