define ('x_ite/Components/Shaders/X3DShaderNode',[
	"x_ite/Components/Shape/X3DAppearanceChildNode",
	"x_ite/Bits/X3DConstants",
	"x_ite/Bits/TraverseType",
],
function (X3DAppearanceChildNode, 
          X3DConstants,
          TraverseType)
{
"use strict";

	function X3DShaderNode (executionContext)
	{
		X3DAppearanceChildNode .call (this, executionContext);

		this .addType (X3DConstants .X3DShaderNode);
	}

	X3DShaderNode .prototype = Object .assign (Object .create (X3DAppearanceChildNode .prototype),
	{
		constructor: X3DShaderNode,
		custom: true,
		setCustom: function (value)
		{
			this .custom = value;
		},
		getCustom: function ()
		{
			return this .custom;
		},
		setGeometryType: function (value)
		{
			this .setShading (value, this .getBrowser () .getBrowserOptions () .Shading_ .getValue ());
		},
		setShading: function (geometryType, shading)
		{
			var gl = this .getBrowser () .getContext ();

			switch (geometryType)
			{
				case 0:
				{
					switch (shading)
					{
						case "POINT":
						case "POINTSET":
						{
							this .primitiveMode = gl .POINTS;
							this .wireframe     = true;
							break;
						}
						case "WIREFRAME":
						{
							this .primitiveMode = gl .POINTS;
							this .wireframe     = true;
							break;
						}
						default:
						{
							// case FLAT:
							// case GOURAUD:
							// case PHONG:
		
							this .primitiveMode = gl .POINTS;
							this .wireframe     = true;
							break;
						}
					}

					break;
				}
				case 1:
				{
					switch (shading)
					{
						case "POINT":
						case "POINTSET":
						{
							this .primitiveMode = gl .POINTS;
							this .wireframe     = true;
							break;
						}
						case "WIREFRAME":
						{
							this .primitiveMode = gl .LINES;
							this .wireframe     = true;
							break;
						}
						default:
						{
							// case FLAT:
							// case GOURAUD:
							// case PHONG:

							this .primitiveMode = gl .LINES;
							this .wireframe     = true;
							break;
						}
					}

					break;
				}
				case 2:
				case 3:
				{
					switch (shading)
					{
						case "POINT":
						case "POINTSET":
						{
							this .primitiveMode = gl .POINTS;
							this .wireframe     = true;
							break;
						}
						case "WIREFRAME":
						{
							this .primitiveMode = gl .LINE_LOOP;
							this .wireframe     = true;
							break;
						}
						default:
						{
							// case FLAT:
							// case GOURAUD:
							// case PHONG:
		
							this .primitiveMode = gl .TRIANGLES;
							this .wireframe     = false;
							break;
						}
					}	

					break;
				}
			}
		},
		traverse: function (type, renderObject)
		{
			switch (type)
			{
				case TraverseType .DISPLAY:
					renderObject .getShaders () .set (this .getId (), this);
					break;
				default:
					break;
			}
		},
	});

	return X3DShaderNode;
});



