						text += "  position " + viewpoint .getUserPosition () .toString () + "\n";
						text += "  orientation " + viewpoint .getUserOrientation () .toString () + "\n";
						text += "  centerOfRotation " + viewpoint .getUserCenterOfRotation () .toString () + "\n";
						text += "}\n";

						console .log (text);

						this .copyToClipboard (text);
