					return false;
	
				if (planes [2] .getDistanceToPoint (center) > radius)
					return false;
	
				if (planes [3] .getDistanceToPoint (center) > radius)
					return false;
	
				if (planes [4] .getDistanceToPoint (center) > radius)
					return false;
	
