				colorNode          = this .getColor (),
				texCoordNode       = this .getTexCoord (),
				normalNode         = this .getNormal (),
				coordNode          = this .getCoord (),
				fogDepthArray      = this .getFogDepths (),
				colorArray         = this .getColors (),
				multiTexCoordArray = this .getMultiTexCoords (),
