define ('standard/Utility/ObjectCache',[],function ()
{
"use strict";

   return function (Type)
   {
	   return {
	      stack: [ ],
	      last: -1,
	      pop: function ()
	      {
				if (this .last > -1)
				{
					var object = this .stack [this .last];
				
					this .last --;

	            return object;
				}

				return new Type ();
	      },
			push: function (object)
	      {
	         this .last ++;
	         return this .stack [this .last] = object;
	      },
			clear: function ()
			{
			   this .stack .length = 0;
			   this .last          = -1;
			},
	   };
   };
});


